INSERT INTO `donnees` (`id`, `titre`, `titre_de`, `contenu`, `contenu_de`, `rang`, `advertise`, `created_at`, `updated_at`) VALUES
(1, 'Remarques', 'Bemerkungen', '<p>Die Vermittler&auml;mter erf&uuml;llen die ihnen in <a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a197" target="_blank">Art. 197 ff. ZPO</a> zugewiesenen Aufgaben (A<a href="http://www.bgs.ar.ch/frontend/versions/872" target="_blank">rt. 2 Abs. 2 Justizgesetz</a>). Das Kantonsgericht entscheidet unter Vorbehalt der Befugnisse der Einzelrichter und Einzelrichterinnen als erste Instanz in Zivilsachen, soweit nicht das Obergericht als einzige kantonale Instanz zust&auml;ndig ist (<a href="http://www.bgs.ar.ch/frontend/versions/872" target="_blank">Art. 13 Justizgesetz</a>)</p>', '<p>Die Vermittler&auml;mter erf&uuml;llen die ihnen in <a href="https://www.admin.ch/opc/de/classified-compilation/20061121/index.html#a197" target="_blank">Art. 197 ff. ZPO</a> zugewiesenen Aufgaben (A<a href="http://www.bgs.ar.ch/frontend/versions/872" target="_blank">rt. 2 Abs. Justizgesetz</a>). Das Kantonsgericht entscheidet unter Vorbehalt der Befugnisse der Einzelrichter und Einzelrichterinnen als erste Instanz in Zivilsachen, soweit nicht das Obergericht als einzige kantonale Instanz zust&auml;ndig ist (<a href="http://www.bgs.ar.ch/frontend/versions/872" target="_blank">Art. 13 Justizgesetz</a>)</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(2, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 2, 9 ff. und 18 ff. des Justizgesetzes (<a href="http://www.bgs.ar.ch/frontend/versions/872" target="_blank">RS 145.31</a>) Art. 2 der Verfassung des Kantons Appenzell A.Rh. (<a href="http://www.bgs.ar.ch/frontend/versions/574" target="_blank">RS 111.1</a>)</p>', '<p>Art. 2, 9 ff. und 18 ff. des Justizgesetzes (<a href="http://www.bgs.ar.ch/frontend/versions/872" target="_blank">RS 145.31</a>) Art. 2 der Verfassung des Kantons Appenzell A.Rh. (<a href="http://www.bgs.ar.ch/frontend/versions/574" target="_blank">RS 111.1</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(3, 'Remarques', 'Bemerkungen', '<p>Schlichtungsbeh&ouml;rde im Sinne von <a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a197" target="_blank">Art. 197 ZPO</a> ist der Vermittler des Bezirks (<a href="https://www.ai.ch/themen/staat-und-recht/gesetzessammlung" target="_blank">art. 3 EG ZPO</a>). Das Bezirksgericht entscheidet als erstinstanzliches Gericht im ordentlichen Verfahren (<a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a219" target="_blank">Art. 219 ff. ZPO</a>), soweit die kantonale Gesetzgebung keine Ausnahmen vorsieht (<a href="https://www.ai.ch/themen/staat-und-recht/gesetzessammlung" target="_blank">art. 6 EG ZPO</a>).</p>', '<p>Schlichtungsbeh&ouml;rde im Sinne von&nbsp;<a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a197" target="_blank">Art. 197 ZPO</a>&nbsp;ist der Vermittler des Bezirks (<a href="https://www.ai.ch/themen/staat-und-recht/gesetzessammlung" target="_blank">art. 3 EG ZPO</a>). Das Bezirksgericht entscheidet als erstinstanzliches Gericht im ordentlichen Verfahren (<a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a219" target="_blank">Art. 219 ff. ZPO</a>), soweit die kantonale Gesetzgebung keine Ausnahmen vorsieht (<a href="https://www.ai.ch/themen/staat-und-recht/gesetzessammlung" target="_blank">art. 6 EG ZPO</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(4, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 3 ff. und 10 ff. des Gerichtsorganisationsgesetzes (GOG, <a href="https://www.ai.ch/themen/staat-und-recht/gesetzessammlung" target="_blank">RS 173.000</a>) Art. 2 ff. des Einf&uuml;hrungsgesetzes zur Schweizerischen Zivilprozessordnung (EG ZPO, <a href="https://www.ai.ch/themen/staat-und-recht/gesetzessammlung" target="_blank">RS 270.000</a>)</p>', '<p>Art. 3 ff. und 10 ff. des Gerichtsorganisationsgesetzes (GOG,&nbsp;<a href="https://www.ai.ch/themen/staat-und-recht/gesetzessammlung" target="_blank">RS 173.000</a>) Art. 2 ff. des Einf&uuml;hrungsgesetzes zur Schweizerischen Zivilprozessordnung (EG ZPO,&nbsp;<a href="https://www.ai.ch/themen/staat-und-recht/gesetzessammlung" target="_blank">RS 270.000</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(5, 'Remarques', 'Bemerkungen', '<p>Jeder Kreis hat einen Friedensrichter und einen Statthalter (<a href="https://gesetzessammlungen.ag.ch/frontend/versions/960" target="_blank">Art. 20 GOG</a>). Die Schlichtungsbeh&ouml;rden nach <a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a197" target="_blank">Art. 197 ZPO</a> sind die Friedensrichterinnen oder Friedensrichter, wenn nichts anderes bestimmt ist (<a href="https://gesetzessammlungen.ag.ch/frontend/versions/1045" target="_blank">Art. 3 und 4 EG ZPO</a>). Das Bezirksgericht entscheidet erstinstanzlich alle Streitigkeiten, die nicht einem anderen Gericht zugewiesen sind (<a href="https://gesetzessammlungen.ag.ch/frontend/versions/1045" target="_blank">Art. 5 EG ZPO</a>).</p>', '<p>Jeder Kreis hat einen Friedensrichter und einen Statthalter (<a href="https://gesetzessammlungen.ag.ch/frontend/versions/960" target="_blank">Art. 20 GOG</a>). Die Schlichtungsbeh&ouml;rden nach&nbsp;<a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a197" target="_blank">Art. 197 ZPO</a>&nbsp;sind die Friedensrichterinnen oder Friedensrichter, wenn nichts anderes bestimmt ist (<a href="https://gesetzessammlungen.ag.ch/frontend/versions/1045" target="_blank">Art. 3 und 4 EG ZPO</a>). Das Bezirksgericht entscheidet erstinstanzlich alle Streitigkeiten, die nicht einem anderen Gericht zugewiesen sind (<a href="https://gesetzessammlungen.ag.ch/frontend/versions/1045" target="_blank">Art. 5 EG ZPO</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(6, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 20 ff., 31 ff., 49 ff. und 66a ff. des Gesetzes &uuml;ber die Organisation der ordentlichen richterlichen Beh&ouml;rden (GOG, <a href="https://gesetzessammlungen.ag.ch/frontend/versions/960" target="_blank">RS 155.100</a>) Art. 2 des Gesetzes &uuml;ber die Bezirks- und Kreiseinteilung (<a href="https://gesetzessammlungen.ag.ch/frontend/versions/1765" target="_blank">RS 117.100</a>) Art. 3 ff. und 10 ff. des Einf&uuml;hrungsgesetzes zur Schweizerischen Zivilprozessordnung (EG ZPO, <a href="https://gesetzessammlungen.ag.ch/frontend/versions/1045" target="_blank">RS 221.200</a>)</p>', '<p>Art. 20 ff., 31 ff., 49 ff. und 66a ff. des Gesetzes &uuml;ber die Organisation der ordentlichen richterlichen Beh&ouml;rden (GOG,&nbsp;<a href="https://gesetzessammlungen.ag.ch/frontend/versions/960" target="_blank">RS 155.100</a>) Art. 2 des Gesetzes &uuml;ber die Bezirks- und Kreiseinteilung (<a href="https://gesetzessammlungen.ag.ch/frontend/versions/1765" target="_blank">RS 117.100</a>) Art. 3 ff. und 10 ff. des Einf&uuml;hrungsgesetzes zur Schweizerischen Zivilprozessordnung (EG ZPO,&nbsp;<a href="https://gesetzessammlungen.ag.ch/frontend/versions/1045" target="_blank">RS 221.200</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(7, 'Remarques', 'Bemerkungen', '<p>Zust&auml;ndig f&uuml;r Schlichtungsversuche sind die Friedensrichterinnen und Friedensrichter im ordentlichen und im vereinfachten Verfahren, soweit es sich nicht um Streitigkeiten gem&auml;ss den Buchstaben b - e handelt (<a href="http://bl.clex.ch/frontend/versions/392" target="_blank">Art. 2 lit. a EG ZPO</a>).</p>', '<p>Zust&auml;ndig f&uuml;r Schlichtungsversuche sind die Friedensrichterinnen und Friedensrichter im ordentlichen und im vereinfachten Verfahren, soweit es sich nicht um Streitigkeiten gem&auml;ss den Buchstaben b - e handelt (<a href="http://bl.clex.ch/frontend/versions/392" target="_blank">Art. 2 lit. a EG ZPO</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(8, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 2, 8 ff., 16 ff. und 18 ff. des Gesetzes &uuml;ber die Organisation der Gerichte (GOG, <a href="http://bl.clex.ch/frontend/versions/994" target="_blank">RS 170</a>) Art. 35 des Gesetzes &uuml;ber die Organisation und die Verwaltung der Gemeinden (<a href="http://bl.clex.ch/frontend/versions/1786" target="_blank">RS 180</a>) Art. 1 ff. des Einf&uuml;hrungsgesetzes zur Schweizerischen Zivilprozessordnung (EG ZPO, <a href="http://bl.clex.ch/frontend/versions/392" target="_blank">RS 221</a>)</p>', '<p>Art. 2, 8 ff., 16 ff. und 18 ff. des Gesetzes &uuml;ber die Organisation der Gerichte (GOG,&nbsp;<a href="http://bl.clex.ch/frontend/versions/994" target="_blank">RS 170</a>) Art. 35 des Gesetzes &uuml;ber die Organisation und die Verwaltung der Gemeinden (<a href="http://bl.clex.ch/frontend/versions/1786" target="_blank">RS 180</a>) Art. 1 ff. des Einf&uuml;hrungsgesetzes zur Schweizerischen Zivilprozessordnung (EG ZPO,&nbsp;<a href="http://bl.clex.ch/frontend/versions/392" target="_blank">RS 221</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(9, 'Remarques', 'Bemerkungen', '<p>Untere Gerichte erster Instanz des Kantons Basel-Stadt sind: in Zivilsachen: Das Zivilgericht bestehend aus den Kammern, dem Dreiergericht und den Einzelgerichten sowie dem Arbeitsgericht (<a href="http://www.gesetzessammlung.bs.ch/frontend/versions/3970" target="_blank">Art. 1 Abs. 1 Ziff. 1 GOG</a>). Als Schlichtungsbeh&ouml;rde des Zivilgerichts, des Appellationsgerichts und des Sozialversicherungsgerichts amten die Gerichtspr&auml;sidentinnen und Gerichtspr&auml;sidenten, die Statthalterinnen und Statthalter sowie die daf&uuml;r gew&auml;hlten Gerichtsschreiberinnen und Gerichtsschreiber des jeweiligen Gerichts (<a href="http://www.gesetzessammlung.bs.ch/frontend/versions/3970" target="_blank">Art. 6 Abs. 2 GOG</a>).</p>', '<p>Untere Gerichte erster Instanz des Kantons Basel-Stadt sind: in Zivilsachen: Das Zivilgericht bestehend aus den Kammern, dem Dreiergericht und den Einzelgerichten sowie dem Arbeitsgericht (<a href="http://www.gesetzessammlung.bs.ch/frontend/versions/3970" target="_blank">Art. 1 Abs. 1 Ziff. 1 GOG</a>). Als Schlichtungsbeh&ouml;rde des Zivilgerichts, des Appellationsgerichts und des Sozialversicherungsgerichts amten die Gerichtspr&auml;sidentinnen und Gerichtspr&auml;sidenten, die Statthalterinnen und Statthalter sowie die daf&uuml;r gew&auml;hlten Gerichtsschreiberinnen und Gerichtsschreiber des jeweiligen Gerichts (<a href="http://www.gesetzessammlung.bs.ch/frontend/versions/3970" target="_blank">Art. 6 Abs. 2 GOG</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(10, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 1 Abs. 1, 5 ff. und 69 ff. des Gerichtsorganisationsgesetzes (GOG, <a href="http://www.gesetzessammlung.bs.ch/frontend/versions/3970" target="_blank">RS 154.100</a>)</p>', '<p>Art. 1 Abs. 1, 5 ff. und 69 ff. des Gerichtsorganisationsgesetzes (GOG,&nbsp;<a href="http://www.gesetzessammlung.bs.ch/frontend/versions/3970" target="_blank">RS 154.100</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(11, 'Remarques', 'Bemerkungen', '<p>Les autorit&eacute;s r&eacute;gionales de conciliation dirigent les tentatives de conciliation au sens des <a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html" target="_blank">articles 197 ss CPC</a> (<a href="https://www.belex.sites.be.ch/frontend/versions/351?locale=fr" target="_blank">art. 10 LiCPM</a>). Le Tribunal r&eacute;gional du Jura bernois-Seeland est dot&eacute; d&rsquo;une agence dans le Jura bernois (<a href="https://www.belex.sites.be.ch/frontend/versions/103?locale=fr" target="_blank">art. 81 al.1 LOJM</a>). L&rsquo;Autorit&eacute; de conciliation du Jura bernois-Seeland est dot&eacute;e d&rsquo;une agence dans le Jura bernois (<a href="https://www.belex.sites.be.ch/frontend/versions/103?locale=fr" target="_blank">art. 84 al. 4 LOJM</a>).</p>', '<p>Die regionalen Schlichtungsbeh&ouml;rden f&uuml;hren die Schlichtungsversuche gem&auml;ss <a href="https://www.admin.ch/opc/de/classified-compilation/20061121/index.html" target="_blank">Artikel 197 ff. ZPO</a> durch&nbsp;(<a href="https://www.belex.sites.be.ch/frontend/versions/351?locale=de" target="_blank">art. 10 EG ZSJ</a>). <span>Das Regionalgericht Berner Jura-Seeland hat eine Aussenstelle im Berner Jura.</span>&nbsp;(<a href="https://www.belex.sites.be.ch/frontend/versions/103?locale=de" target="_blank">Art. 81 Abs. 1 GSOG</a>). <span>Die Schlichtungsbeh&ouml;rde Berner Jura-Seeland verf&uuml;gt &uuml;ber eine Aussenstelle im Berner Jura</span>&nbsp;(<a href="https://www.belex.sites.be.ch/frontend/versions/103?locale=de" target="_blank">Art. 84 Abs. 4 GSOG</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(12, 'Bases légales', 'Rechtliche Grundlagen', '<p>art. 2, 35 ss, 80 ss et 84 ss de la loi sur l&rsquo;organisation des autorit&eacute;s judiciaires et du Minist&egrave;re public (LOJM, <a href="https://www.belex.sites.be.ch/frontend/versions/103?locale=fr" target="_blank">RS 161.1</a>) art. 39a et annexe 2 de la loi sur l''organisation du Conseil ex&eacute;cutif et de l''administration (LOCA, <a href="https://www.belex.sites.be.ch/frontend/versions/1032?locale=fr" target="_blank">RS 152.01</a>) art. 5 ss de la loi portant introduction du code de proc&eacute;dure civile, du code de proc&eacute;dure p&eacute;nale et de la loi sur la proc&eacute;dure p&eacute;nale applicable aux mineurs (LiCPM, <a href="https://www.belex.sites.be.ch/frontend/versions/351?locale=fr" target="_blank">RS 271.1</a>)</p>', '<p>Art. 2, 35 ff., 80 ff. und 84 ff. des&nbsp;Gesetzes &uuml;ber die Organisation der Gerichtsbeh&ouml;rden und der Staatsanwaltschaft (GSOG, <a href="https://www.belex.sites.be.ch/frontend/versions/103?locale=de" target="_blank">RS 161.1</a>) Art. 39a und Anhang 2 des&nbsp;Gesetzes &uuml;ber die Organisation des Regierungsrates und der Verwaltung (OrG, <a href="https://www.belex.sites.be.ch/frontend/versions/1032?locale=de" target="_blank">RS 152.01</a>) Art. 5 ff. des Einf&uuml;hrungsgesetzes zur Zivilprozessordnung, zur Strafprozessordnung und zur Jugendstrafprozessordnung&nbsp;(EG ZSJ, <a href="https://www.belex.sites.be.ch/frontend/versions/351?locale=de" target="_blank">RS 271.1</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(13, 'Remarques', 'Bemerkungen', '<p>Un pr&eacute;sident ou une pr&eacute;sidente du tribunal d&rsquo;arrondissement est l&rsquo;autorit&eacute; de conciliation au sens des articles <a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a197" target="_blank">197 et suivants CPC</a> (<a href="https://bdlf.fr.ch/">art. 60 al. 1 LJ</a>).&nbsp;La justice de paix est l&rsquo;autorit&eacute; de protection de l&rsquo;enfant et de l&rsquo;adulte au sens du code civil suisse. Le ou la juge de paix exerce la juridiction gracieuse dans le domaine des successions. Il ou elle exerce en outre les comp&eacute;tences qui lui sont attribu&eacute;es par la l&eacute;gislation sp&eacute;ciale, notamment dans le domaine de la protection de l&rsquo;enfant et de l&rsquo;adulte et dans celui des droits r&eacute;els (<a href="https://bdlf.fr.ch/">art. 58 LJ</a>)</p>', '<p>Schlichtungsbeh&ouml;rde im Sinne der <a href="https://www.admin.ch/opc/de/classified-compilation/20061121/index.html#a197">Artikel 197 ff. ZPO</a> ist eine Pr&auml;sidentin oder ein Pr&auml;sident des in der Hauptsache zust&auml;ndigen Gerichts&nbsp;(<a href="http://bdlf.de.ch">Art. 60 Abs. 1 JG</a>).&nbsp;Das Friedensgericht ist die Kindes- und Erwachsenenschutzbeh&ouml;rde im Sinne des Schweizerischen Zivilgesetzbuches. Die Friedensrichterin oder der Friedensrichter &uuml;bt die freiwillige Gerichtsbarkeit in Erbschaftssachen aus. Sie oder er ist zudem f&uuml;r die Bereiche zust&auml;ndig, die ihr oder ihm durch die Spezialgesetzgebung zugewiesen werden, insbesondere f&uuml;r den Kindes- und Erwachsenenschutz und die dinglichen Rechte (<a href="http://bdlf.de.ch">Art. 58 JG</a>)</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(14, 'Bases légales', 'Rechtliche Grundlagen', '<p>art. 3, 32 ss et 50 ss de la loi sur la justice du 31 mai 2010 (LJ, <a href="https://bdlf.fr.ch/" target="_blank">RS 130.1</a>) art. 1 ss de la loi d&eacute;terminant le nombre et la circonscription des districts administratifs du 11 f&eacute;vrier 1988 (<a href="https://bdlf.fr.ch/" target="_blank">RS 112.5</a>) art. 1 ss de l''Ordonnance indiquant les noms des communes et leur rattachement aux districts administratifs du 24 novembre 2015 (ONCD, <a href="https://bdlf.fr.ch/" target="_blank">RS 112.51</a>)</p>', '<p>Art. 3, 32 ff. und 50 ff. des Justizgesetzes vom 31. Mai 2010&nbsp;(JG,&nbsp;<a href="http://bdlf.de.ch" target="_blank">RS 130.1</a>) Art. 1 ff. des Gesetzes vom 11. Februar 1988&nbsp;&uuml;ber die Verwaltungsbezirke (<a href="http://bdlf.de.ch" target="_blank">RS 112.5</a>) Art. 1 ff. der Verordnung vom 24. November 2015 &uuml;ber die Namen der Gemeinden und deren Zugeh&ouml;rigkeit zu den Verwaltungsbezirken (NGBV,&nbsp;<a href="http://bdlf.de.ch" target="_blank">RS 112.51</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(15, 'Remarques', 'Bemerkungen', '<p>Le Tribunal de premi&egrave;re instance exerce notamment la comp&eacute;tence que le CPC attribue &agrave; l&rsquo;autorit&eacute; de conciliation (<a href="http://www.ge.ch/legislation/rsg/f/rsg_e2_05.html" target="_blank">art. 86 LOJ</a>). Ainsi, les conciliations pr&eacute;alables aux proc&eacute;dures judiciaires sont du ressort du Tribunal de premi&egrave;re instance. La Justice de paix est charg&eacute;e des t&acirc;ches relatives &agrave; la d&eacute;volution des successions, au sens de la LaCC (<a href="https://www.ge.ch/legislation/rsg/f/rsg_e1_05.html" target="_blank">RS E 1 05</a>). Elle ne peut pas rendre de jugement sur le fond d''un litige successoral, cette comp&eacute;tence appartenant au Tribunal de premi&egrave;re instance (actions en annulation de testament, en partage, en p&eacute;tition d''h&eacute;r&eacute;dit&eacute;, etc.). Conciliation volontaire (uniquement) : en application de l''<a href="http://www.ge.ch/legislation/rsg/f/rsg_e2_05.html" target="_blank">art. 109 LOJ</a>, le juge de paix est charg&eacute; de concilier les parties qui en font la demande.</p>', '<p>Le Tribunal de premi&egrave;re instance exerce notamment la comp&eacute;tence que le CPC attribue &agrave; l&rsquo;autorit&eacute; de conciliation (<a href="http://www.ge.ch/legislation/rsg/f/rsg_e2_05.html" target="_blank">art. 86 LOJ</a>). Ainsi, les conciliations pr&eacute;alables aux proc&eacute;dures judiciaires sont du ressort du Tribunal de premi&egrave;re instance. La Justice de paix est charg&eacute;e des t&acirc;ches relatives &agrave; la d&eacute;volution des successions, au sens de la LaCC (<a href="https://www.ge.ch/legislation/rsg/f/rsg_e1_05.html" target="_blank">RS E 1 05</a>). Elle ne peut pas rendre de jugement sur le fond d''un litige successoral, cette comp&eacute;tence appartenant au Tribunal de premi&egrave;re instance (actions en annulation de testament, en partage, en p&eacute;tition d''h&eacute;r&eacute;dit&eacute;, etc.). Conciliation volontaire (uniquement) : en application de l''<a href="http://www.ge.ch/legislation/rsg/f/rsg_e2_05.html" target="_blank">art. 109 LOJ</a>, le juge de paix est charg&eacute; de concilier les parties qui en font la demande.</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(16, 'Bases légales', 'Rechtliche Grundlagen', '<p>art. 1, 83 ss, 103 ss, 106 ss, 119 ss et 135 ss de la loi sur l&rsquo;organisation judiciaire (LOJ, <a href="http://www.ge.ch/legislation/rsg/f/rsg_e2_05.html" target="_blank">RS E 2 05</a>) art. 1 de la loi sur l&rsquo;administration des communes (LAC, <a href="http://www.ge.ch/legislation/rsg/f/rsg_b6_05.html" target="_blank">RS B 6 05</a>)</p>', '<p>art. 1, 83 ss, 103 ss, 106 ss, 119 ss et 135 ss de la loi sur l&rsquo;organisation judiciaire (LOJ,&nbsp;<a href="http://www.ge.ch/legislation/rsg/f/rsg_e2_05.html" target="_blank">RS E 2 05</a>) art. 1 de la loi sur l&rsquo;administration des communes (LAC,&nbsp;<a href="http://www.ge.ch/legislation/rsg/f/rsg_b6_05.html" target="_blank">RS B 6 05</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(17, 'Remarques', 'Bemerkungen', '<p>In jeder Gemeinde besteht ein Vermittleramt (<a href="http://gesetze.gl.ch/frontend/versions/1719" target="_blank">art. 4 Gerichtsorganisationsgesetz</a>). Das Vermittleramt f&uuml;hrt alle Schlichtungsverfahren durch, welche dieses Gesetz nicht einer anderen Schlichtungsbeh&ouml;rde zuweist (<a href="http://gesetze.gl.ch/frontend/versions/739" target="_blank">art. 10 EG ZPO</a>). Die Zivilkammern behandeln alle dem Kantonsgericht durch Gesetz zugewiesenen Gesch&auml;fte mit Ausnahme der Strafsachen. Sie sind insbesondere zust&auml;ndig zur erstinstanzlichen Beurteilung zivilrechtlicher Streitigkeiten, soweit das Gesetz keine andere Beh&ouml;rde als kompetent erkl&auml;rt (<a href="http://gesetze.gl.ch/frontend/versions/1719" target="_blank">art. 13 Gerichtsorganisationsgesetz</a>).</p>', '<p>In jeder Gemeinde besteht ein Vermittleramt (<a href="http://gesetze.gl.ch/frontend/versions/1719" target="_blank">Art. 4 Gerichtsorganisationsgesetz</a>). Das Vermittleramt f&uuml;hrt alle Schlichtungsverfahren durch, welche dieses Gesetz nicht einer anderen Schlichtungsbeh&ouml;rde zuweist (<a href="http://gesetze.gl.ch/frontend/versions/739" target="_blank">Art. 10 EG ZPO</a>). Die Zivilkammern behandeln alle dem Kantonsgericht durch Gesetz zugewiesenen Gesch&auml;fte mit Ausnahme der Strafsachen. Sie sind insbesondere zust&auml;ndig zur erstinstanzlichen Beurteilung zivilrechtlicher Streitigkeiten, soweit das Gesetz keine andere Beh&ouml;rde als kompetent erkl&auml;rt (<a href="http://gesetze.gl.ch/frontend/versions/1719" target="_blank">Art. 13 Gerichtsorganisationsgesetz</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(18, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 4 ff., 8 ff. und 15 ff. des Gesetzes &uuml;ber die Gerichtsorganisation des Kantons Glarus (<a href="http://gesetze.gl.ch/frontend/versions/1719" target="_blank">RS III A/2</a>) Art. 3 ff. und 10 des Einf&uuml;hrungsgesetzes zur Schweizerischen Zivilprozessordnung (EG ZPO, <a href="http://gesetze.gl.ch/frontend/versions/739" target="_blank">RS III C/1</a>) Art. 24 des Gesetzes &uuml;ber die Wahlen und Abstimmungen an der Urne (<a href="http://gesetze.gl.ch/frontend/versions/1607" target="_blank">RS I D/22/2</a>) Art. 148 der Verfassung des Kantons Glarus (<a href="http://gesetze.gl.ch/frontend/versions/1628" target="_blank">RS I A/1/1</a>)</p>', '<p>Art. 4 ff., 8 ff. und 15 ff. des Gesetzes &uuml;ber die Gerichtsorganisation des Kantons Glarus (<a href="http://gesetze.gl.ch/frontend/versions/1719" target="_blank">RS III A/2</a>) Art. 3 ff. und 10 des Einf&uuml;hrungsgesetzes zur Schweizerischen Zivilprozessordnung (EG ZPO,&nbsp;<a href="http://gesetze.gl.ch/frontend/versions/739" target="_blank">RS III C/1</a>) Art. 24 des Gesetzes &uuml;ber die Wahlen und Abstimmungen an der Urne (<a href="http://gesetze.gl.ch/frontend/versions/1607" target="_blank">RS I D/22/2</a>) Art. 148 der Verfassung des Kantons Glarus (<a href="http://gesetze.gl.ch/frontend/versions/1628" target="_blank">RS I A/1/1</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(19, 'Remarques', 'Bemerkungen', '<p>Die Aufgaben der Schlichtungsbeh&ouml;rde gem&auml;ss Zivilprozessordnung obliegen: dem Vermittleramt, soweit nicht eine andere Schlichtungsbeh&ouml;rde zust&auml;ndig ist; der Schlichtungsbeh&ouml;rde f&uuml;r Mietsachen bei Streitigkeiten aus Miete und Pacht von Wohn- und Gesch&auml;ftsr&auml;umen; der Schlichtungsbeh&ouml;rde f&uuml;r Gleichstellungssachen bei Streitigkeiten nach dem bundesrechtlichen Gleichstellungsgesetz. Die &ouml;rtliche Zust&auml;ndigkeit der Schlichtungsbeh&ouml;rde richtet sich nach den Bestimmungen der Zivilprozessordnung &uuml;ber den Gerichtsstand (<a href="http://www.gr-lex.gr.ch/frontend/versions/2704" target="_blank">art. 3 EGzZPO</a>). Als Schlichtungsbeh&ouml;rde besteht in jedem Bezirk ein Vermittleramt. Das Vermittleramt ist fachlich eigenst&auml;ndig. In administrativer Hinsicht ist es dem Bezirksgericht angegliedert. Die Einzelheiten regeln Vermittleramt und Bezirksgericht in einer Vereinbarung (<a href="http://www.gr-lex.gr.ch/frontend/versions/2698" target="_blank">Art. 45 GOG</a>).&nbsp;F&uuml;r Streitigkeiten aus Miete und Pacht von Wohn- und Gesch&auml;ftsr&auml;umen besteht in jedem Bezirk eine Schlichtungsbeh&ouml;rde (<a href="http://www.gr-lex.gr.ch/frontend/versions/2698" target="_blank">Art. 51 GOG</a>).</p>', '<p>Die Aufgaben der Schlichtungsbeh&ouml;rde gem&auml;ss Zivilprozessordnung obliegen: dem Vermittleramt, soweit nicht eine andere Schlichtungsbeh&ouml;rde zust&auml;ndig ist; der Schlichtungsbeh&ouml;rde f&uuml;r Mietsachen bei Streitigkeiten aus Miete und Pacht von Wohn- und Gesch&auml;ftsr&auml;umen; der Schlichtungsbeh&ouml;rde f&uuml;r Gleichstellungssachen bei Streitigkeiten nach dem bundesrechtlichen Gleichstellungsgesetz. Die &ouml;rtliche Zust&auml;ndigkeit der Schlichtungsbeh&ouml;rde richtet sich nach den Bestimmungen der Zivilprozessordnung &uuml;ber den Gerichtsstand (<a href="http://www.gr-lex.gr.ch/frontend/versions/2704" target="_blank">art. 3 EGzZPO</a>). Als Schlichtungsbeh&ouml;rde besteht in jedem Bezirk ein Vermittleramt. Das Vermittleramt ist fachlich eigenst&auml;ndig. In administrativer Hinsicht ist es dem Bezirksgericht angegliedert. Die Einzelheiten regeln Vermittleramt und Bezirksgericht in einer Vereinbarung (<a href="http://www.gr-lex.gr.ch/frontend/versions/2698" target="_blank">Art. 45 GOG</a>).&nbsp;F&uuml;r Streitigkeiten aus Miete und Pacht von Wohn- und Gesch&auml;ftsr&auml;umen besteht in jedem Bezirk eine Schlichtungsbeh&ouml;rde (<a href="http://www.gr-lex.gr.ch/frontend/versions/2698" target="_blank">Art. 51 GOG</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(20, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 3, 17 ff., 35 ff., 45 ff., 51 ff. und 57 ff. Gerichtsorganisationsgesetz (GOG,&nbsp;<a href="http://www.gr-lex.gr.ch/frontend/versions/2698" target="_blank">BR 173.000</a>) Art. 68 und 71 Verfassung des Kantons Graub&uuml;nden (<a href="http://www.gr-lex.gr.ch/frontend/versions/2752" target="_blank">BR 110.100</a>) Art. 1 Gesetz &uuml;ber Einteilung des Kantons Graub&uuml;nden in Regionen (<a href="http://www.gr-lex.gr.ch/frontend/versions/2766" target="_blank">BR 110.200</a>) Art. 1 ff. Einf&uuml;hrungsgesetz zur Schweizerischen Zivilprozessordnung (EGzZPO,&nbsp;<a href="http://www.gr-lex.gr.ch/frontend/versions/2704" target="_blank">BR 320.100</a>)</p>', '<p>Art. 3, 17 ff., 35 ff., 45 ff., 51 ff. und 57 ff. Gerichtsorganisationsgesetz (GOG,&nbsp;<a href="http://www.gr-lex.gr.ch/frontend/versions/2698" target="_blank">BR 173.000</a>) Art. 68 und 71 Verfassung des Kantons Graub&uuml;nden (<a href="http://www.gr-lex.gr.ch/frontend/versions/2752" target="_blank">BR 110.100</a>) Art. 1 Gesetz &uuml;ber Einteilung des Kantons Graub&uuml;nden in Regionen (<a href="http://www.gr-lex.gr.ch/frontend/versions/2766" target="_blank">BR 110.200</a>) Art. 1 ff. Einf&uuml;hrungsgesetz zur Schweizerischen Zivilprozessordnung (EGzZPO,&nbsp;<a href="http://www.gr-lex.gr.ch/frontend/versions/2704" target="_blank">BR 320.100</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(21, 'Remarques', 'Bemerkungen', '<p>Lorsque la proc&eacute;dure au fond doit &ecirc;tre pr&eacute;c&eacute;d&eacute;e d''une tentative de conciliation, le juge civil du Tribunal de premi&egrave;re instance est autorit&eacute; de conciliation dans les causes dont il conna&icirc;t. Il peut d&eacute;l&eacute;guer cette t&acirc;che au greffier (<a href="https://rsju.jura.ch/fr/viewdocument.html?idn=20043&amp;id=36702" target="_blank">art. 6 LiCPC</a>).</p>', '<p>Lorsque la proc&eacute;dure au fond doit &ecirc;tre pr&eacute;c&eacute;d&eacute;e d''une tentative de conciliation, le juge civil du Tribunal de premi&egrave;re instance est autorit&eacute; de conciliation dans les causes dont il conna&icirc;t. Il peut d&eacute;l&eacute;guer cette t&acirc;che au greffier (<a href="https://rsju.jura.ch/fr/viewdocument.html?idn=20043&amp;id=36702" target="_blank">art. 6 LiCPC</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(22, 'Bases légales', 'Rechtliche Grundlagen', '<p>art. 4, 14, 20 ss, 29 et 32 de la loi d''organisation judiciaire (<a href="https://rsju.jura.ch/fr/viewdocument.html?idn=20024&amp;id=36876" target="_blank">LOJ, RS 181.1</a>) art. 4 ss de la loi d''introduction du Code de proc&eacute;dure civile suisse (<a href="https://rsju.jura.ch/fr/viewdocument.html?idn=20043&amp;id=36702" target="_blank">LiCPC, RS 271.1</a>)</p>', '<p>art. 4, 14, 20 ss, 29 et 32 de la loi d''organisation judiciaire (<a href="https://rsju.jura.ch/fr/viewdocument.html?idn=20024&amp;id=36876" target="_blank">LOJ, RS 181.1</a>) art. 4 ss de la loi d''introduction du Code de proc&eacute;dure civile suisse (<a href="https://rsju.jura.ch/fr/viewdocument.html?idn=20043&amp;id=36702" target="_blank">LiCPC, RS 271.1</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(23, 'Remarques', 'Bemerkungen', 'Dem gerichtlichen Entscheidverfahren geht ein Schlichtungsversuch voraus. Zuständig sind die Friedensrichter der entsprechenden Gerichtsbezirke (Luzern, Kriens, Hochdorf und Willisau).', 'Dem gerichtlichen Entscheidverfahren geht ein Schlichtungsversuch voraus. Zuständig sind die Friedensrichter der entsprechenden Gerichtsbezirke (Luzern, Kriens, Hochdorf und Willisau).', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(24, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 3, 5, 14 ff., 23 ff., 31 ff., 38 ff. und 45 ff. des Gesetzes &uuml;ber die Organisation der Gerichte und Beh&ouml;rden in Zivil und Strafverfahren (<a href="http://srl.lu.ch/frontend/versions/3002" target="_blank">JusG, RS 260</a>) Art. 1 ff. der Kantonsratsbeschluss &uuml;ber die Sitze der Gerichte und Schlichtungsbeh&ouml;rden und die Einteilung des Kantons in Gerichtsbezirke (<a href="http://srl.lu.ch/frontend/versions/2820" target="_blank">RS 261</a>)</p>', '<p>Art. 3, 5, 14 ff., 23 ff., 31 ff., 38 ff. und 45 ff. des Gesetzes &uuml;ber die Organisation der Gerichte und Beh&ouml;rden in Zivil und Strafverfahren (<a href="http://srl.lu.ch/frontend/versions/3002" target="_blank">JusG, RS 260</a>) Art. 1 ff. der Kantonsratsbeschluss &uuml;ber die Sitze der Gerichte und Schlichtungsbeh&ouml;rden und die Einteilung des Kantons in Gerichtsbezirke (<a href="http://srl.lu.ch/frontend/versions/2820" target="_blank">RS 261</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(25, 'Remarques', 'Bemerkungen', '<p>Le Tribunal d&rsquo;instance est compos&eacute; notamment de la Chambre de conciliation (<a href="http://rsn.ne.ch/DATA/program/books/rsne/htm/1611.htm" target="_blank">art. 7 OJN</a>). La Chambre de conciliation tente de trouver un accord entre les parties de mani&egrave;re informelle. Elle assume les t&acirc;ches qui lui sont attribu&eacute;es par le code de proc&eacute;dure civile (<a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html" target="_blank">CPC</a>), du 19 d&eacute;cembre 2008, et par d''autres lois (<a href="http://rsn.ne.ch/DATA/program/books/rsne/htm/1611.htm" target="_blank">art. 14 OJN</a>).</p>', '<p>Le Tribunal d&rsquo;instance est compos&eacute; notamment de la Chambre de conciliation (<a href="http://rsn.ne.ch/DATA/program/books/rsne/htm/1611.htm" target="_blank">art. 7 OJN</a>). La Chambre de conciliation tente de trouver un accord entre les parties de mani&egrave;re informelle. Elle assume les t&acirc;ches qui lui sont attribu&eacute;es par le code de proc&eacute;dure civile (<a href="https://www.admin.ch/opc/de/classified-compilation/20061121/index.html" target="_blank">CPC</a>), du 19 d&eacute;cembre 2008, et par d''autres lois (<a href="http://rsn.ne.ch/DATA/program/books/rsne/htm/1611.htm" target="_blank">art. 14 OJN</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(26, 'Bases légales', 'Rechtliche Grundlagen', '<p>art. 6 ss, 8, 15 ss, 18 ss, 33 ss et 98a ss de loi d''organisation judiciaire neuch&acirc;teloise (OJN, <a href="http://rsn.ne.ch/DATA/program/books/rsne/htm/1611.htm" target="_blank">RS 161.1</a>) art. 2 de la loi sur les communes (LCo, <a href="http://rsn.ne.ch/DATA/program/books/rsne/htm/1711.htm" target="_blank">RS 171.1</a>)</p>', '<p>art. 6 ss, 8, 15 ss, 18 ss, 33 ss et 98a ss de loi d''organisation judiciaire neuch&acirc;teloise (OJN,&nbsp;<a href="http://rsn.ne.ch/DATA/program/books/rsne/htm/1611.htm" target="_blank">RS 161.1</a>) art. 2 de la loi sur les communes (LCo,&nbsp;<a href="http://rsn.ne.ch/DATA/program/books/rsne/htm/1711.htm" target="_blank">RS 171.1</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(27, 'Remarques', 'Bemerkungen', '<p>F&uuml;r den Kanton besteht eine Schlichtungsbeh&ouml;rde. Die Schlichtungsbeh&ouml;rde f&uuml;hrt die Schlichtungsversuche gem&auml;ss <a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a197" target="_blank">Art. 197 ff. ZPO</a> durch. Sie ist Rechtsberatungsstelle gem&auml;ss <a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a201" target="_blank">Art. 201 Abs. 2 ZPO</a>. Die Beratung ist unentgeltlich. Sie erf&uuml;llt die weiteren ihr durch die Gesetzgebung zugewiesenen Aufgaben (<a href="http://www.erlasse.ch/link.php?s=nw&amp;g=261.1" target="_blank">Art. 40 und 41 GerG</a>).</p>', '<p>F&uuml;r den Kanton besteht eine Schlichtungsbeh&ouml;rde. Die Schlichtungsbeh&ouml;rde f&uuml;hrt die Schlichtungsversuche gem&auml;ss&nbsp;<a href="https://www.admin.ch/opc/de/classified-compilation/20061121/index.html#a197" target="_blank">Art. 197 ff. ZPO</a>&nbsp;durch. Sie ist Rechtsberatungsstelle gem&auml;ss&nbsp;<a href="https://www.admin.ch/opc/de/classified-compilation/20061121/index.html#a201" target="_blank">Art. 201 Abs. 2 ZPO</a>. Die Beratung ist unentgeltlich. Sie erf&uuml;llt die weiteren ihr durch die Gesetzgebung zugewiesenen Aufgaben (<a href="http://www.erlasse.ch/link.php?s=nw&amp;g=261.1" target="_blank">Art. 40 und 41 GerG</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(28, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 3, 6 ff., 20 ff. und 40 ff. Gesetz &uuml;ber die Gerichte und die Justizbeh&ouml;rden (GerG, <a href="http://www.erlasse.ch/link.php?s=nw&amp;g=261.1" target="_blank">RS 261.1</a>) Art. 4 Gesetz &uuml;ber Organisation und Verwaltung der Gemeinden (GemG, <a href="http://www.erlasse.ch/link.php?s=nw&amp;g=171.1" target="_blank">RS 171.1</a>).</p>', '<p>Art. 3, 6 ff., 20 ff. und 40 ff. Gesetz &uuml;ber die Gerichte und die Justizbeh&ouml;rden (GerG,&nbsp;<a href="http://www.erlasse.ch/link.php?s=nw&amp;g=261.1" target="_blank">RS 261.1</a>) Art. 4 Gesetz &uuml;ber Organisation und Verwaltung der Gemeinden (GemG,&nbsp;<a href="http://www.erlasse.ch/link.php?s=nw&amp;g=171.1" target="_blank">RS 171.1</a>).</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(29, 'Remarques', 'Bemerkungen', '<p>Die Schlichtungsbeh&ouml;rde besteht aus dem Pr&auml;sidium und dem Vizepr&auml;sidium und mindestens sechs Mitgliedern. Die Besetzung der Schlichtungsbeh&ouml;rde legt im Einzelfall das Pr&auml;sidium und im Rahmen der ihm zugewiesenen F&auml;lle das Vizepr&auml;sidium fest. In den F&auml;llen nach <a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a200" target="_blank">Art. 200 der Zivilprozessordung</a> amtet die Schlichtungsbeh&ouml;rde mit dem Pr&auml;sidium oder dem Vizepr&auml;sidium und zwei Mitgliedern gem&auml;ss der geforderten Parit&auml;t (<a href="http://gdb.ow.ch/frontend/versions/936" target="_blank">art. 6 Gesetz &uuml;ber die Gerichtsorganisation</a>).</p>', '<p>Die Schlichtungsbeh&ouml;rde besteht aus dem Pr&auml;sidium und dem Vizepr&auml;sidium und mindestens sechs Mitgliedern. Die Besetzung der Schlichtungsbeh&ouml;rde legt im Einzelfall das Pr&auml;sidium und im Rahmen der ihm zugewiesenen F&auml;lle das Vizepr&auml;sidium fest. In den F&auml;llen nach&nbsp;<a href="https://www.admin.ch/opc/de/classified-compilation/20061121/index.html#a200" target="_blank">Art. 200 der Zivilprozessordung</a>&nbsp;amtet die Schlichtungsbeh&ouml;rde mit dem Pr&auml;sidium oder dem Vizepr&auml;sidium und zwei Mitgliedern gem&auml;ss der geforderten Parit&auml;t (<a href="http://gdb.ow.ch/frontend/versions/936" target="_blank">art. 6 Gesetz &uuml;ber die Gerichtsorganisation</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(30, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 1 ff. und 34 ff. Gesetz &uuml;ber die Gerichtsorganisation (<a href="http://gdb.ow.ch/frontend/versions/936" target="_blank">RS 134.1</a>) Art. 1 ff. Ausf&uuml;hrungsbestimmungen &uuml;ber die Schlichtungsbeh&ouml;rde (<a href="http://gdb.ow.ch/frontend/versions/602" target="_blank">RS 134.118</a>) Art. 2 Verfassung des Kantons Obwalden (<a href="http://gdb.ow.ch/frontend/versions/970" target="_blank">RS 101</a>)</p>', '<p>Art. 1 ff. und 34 ff. Gesetz &uuml;ber die Gerichtsorganisation (<a href="http://gdb.ow.ch/frontend/versions/936" target="_blank">RS 134.1</a>) Art. 1 ff. Ausf&uuml;hrungsbestimmungen &uuml;ber die Schlichtungsbeh&ouml;rde (<a href="http://gdb.ow.ch/frontend/versions/602" target="_blank">RS 134.118</a>) Art. 2 Verfassung des Kantons Obwalden (<a href="http://gdb.ow.ch/frontend/versions/970" target="_blank">RS 101</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(31, 'Remarques', 'Bemerkungen', '<p>Der Kanton Schaffhausen besteht aus h&ouml;chstens vier Friedensrichterkreisen mit je einem Friedensrichteramt. Dieses hat seinen Sitz am Kreishauptort. Das Friedensrichteramt ist die zust&auml;ndige Schlichtungsbeh&ouml;rde f&uuml;r die Gemeinden des jeweiligen Kreises bei streitigen Zivilsachen, soweit hierf&uuml;r nicht eine besondere Schlichtungsbeh&ouml;rde besteht (<a href="http://rechtsbuch.sh.ch/index.php?id=10156" target="_blank">Art. 9 JG</a>). Das Kantonsgericht behandelt als erste Instanz zivilrechtliche Angelegenheiten, soweit nicht das Obergericht als einzige kantonale Instanz zust&auml;ndig ist (<a href="http://rechtsbuch.sh.ch/index.php?id=10156" target="_blank">Art. 28 JG</a>).</p>', '<p>Der Kanton Schaffhausen besteht aus h&ouml;chstens vier Friedensrichterkreisen mit je einem Friedensrichteramt. Dieses hat seinen Sitz am Kreishauptort. Das Friedensrichteramt ist die zust&auml;ndige Schlichtungsbeh&ouml;rde f&uuml;r die Gemeinden des jeweiligen Kreises bei streitigen Zivilsachen, soweit hierf&uuml;r nicht eine besondere Schlichtungsbeh&ouml;rde besteht (<a href="http://rechtsbuch.sh.ch/index.php?id=10156" target="_blank">Art. 9 JG</a>). Das Kantonsgericht behandelt als erste Instanz zivilrechtliche Angelegenheiten, soweit nicht das Obergericht als einzige kantonale Instanz zust&auml;ndig ist (<a href="http://rechtsbuch.sh.ch/index.php?id=10156" target="_blank">Art. 28 JG</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(32, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 9 ff., 26 ff. und 38 ff. Justizgesetz (JG, RS <a href="http://rechtsbuch.sh.ch/index.php?id=10156" target="_blank">173.200</a>) Art. 1 Verordnung &uuml;ber die Friedensrichterkreise (RS <a href="http://rechtsbuch.sh.ch/index.php?id=10156" target="_blank">173.201</a>) Art. 77 und 78 Verfassung des Kantons Schaffhausen (RS <a href="http://rechtsbuch.sh.ch/index.php?id=10091" target="_blank">101.000</a>)</p>', '<p>Art. 9 ff., 26 ff. und 38 ff. Justizgesetz (JG, RS&nbsp;<a href="http://rechtsbuch.sh.ch/index.php?id=10156" target="_blank">173.200</a>) Art. 1 Verordnung &uuml;ber die Friedensrichterkreise (RS&nbsp;<a href="http://rechtsbuch.sh.ch/index.php?id=10156" target="_blank">173.201</a>) Art. 77 und 78 Verfassung des Kantons Schaffhausen (RS&nbsp;<a href="http://rechtsbuch.sh.ch/index.php?id=10091" target="_blank">101.000</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(33, 'Remarques', 'Bemerkungen', '<p>Die Vermittler&auml;mter sind als Schlichtungsbeh&ouml;rden f&uuml;r alle Schlichtungsverfahren zust&auml;ndig, die nicht einer anderen Beh&ouml;rde &uuml;bertragen sind. Die Schlichtungsbeh&ouml;rden in Mietsachen sind f&uuml;r die gesetzlich vorgesehenen Mietverfahren zust&auml;ndig. Die Schlichtungsstelle f&uuml;r Diskriminierungsstreitigkeiten ist f&uuml;r die vorgesehenen Verfahren nach der Gleichstellungsgesetzgebung zust&auml;ndig (<a href="https://www.sz.ch/behoerden/amtsblatt-gesetze-entscheide/gesetzsammlung/systematische-srsz.html/72-416-413-1366-1365#anchor-19270" target="_blank">Art. 69 Justizverordnung</a>). Jede der 30 Gemeinden des Kt. Schwyz hat einen Vermittler/Friedensrichter und einen Stellvertreter.</p>', '<p>Die Vermittler&auml;mter sind als Schlichtungsbeh&ouml;rden f&uuml;r alle Schlichtungsverfahren zust&auml;ndig, die nicht einer anderen Beh&ouml;rde &uuml;bertragen sind. Die Schlichtungsbeh&ouml;rden in Mietsachen sind f&uuml;r die gesetzlich vorgesehenen Mietverfahren zust&auml;ndig. Die Schlichtungsstelle f&uuml;r Diskriminierungsstreitigkeiten ist f&uuml;r die vorgesehenen Verfahren nach der Gleichstellungsgesetzgebung zust&auml;ndig (<a href="https://www.sz.ch/behoerden/amtsblatt-gesetze-entscheide/gesetzsammlung/systematische-srsz.html/72-416-413-1366-1365#anchor-19270" target="_blank">Art. 69 Justizverordnung</a>). Jede der 30 Gemeinden des Kt. Schwyz hat einen Vermittler/Friedensrichter und einen Stellvertreter.</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(34, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 4, 6, 10 ff., 29 ff. und 69 Justizverordnung (<a href="https://www.sz.ch/behoerden/amtsblatt-gesetze-entscheide/gesetzsammlung/systematische-srsz.html/72-416-413-1366-1365#anchor-19270" target="_blank">RS 231.110</a>) Art. 22, 23 und 25 Verfassung des eidgen&ouml;ssischen Standes Schwyz (<a href="https://www.sz.ch/behoerden/amtsblatt-gesetze-entscheide/gesetzsammlung/systematische-srsz.html/72-416-413-1366-1365#Anchor-42977" target="_blank">RS 100.000</a>) Art. 3&nbsp;Gesetz &uuml;ber die Organisation der Gemeinden und Bezirke (GOG, <a href="https://www.sz.ch/behoerden/amtsblatt-gesetze-entscheide/gesetzsammlung/systematische-srsz.html/72-416-413-1366-1365#Anchor-42977" target="_blank">RS 152.100</a>)</p>', '<p>Art. 4, 6, 10 ff., 29 ff. und 69 Justizverordnung (<a href="https://www.sz.ch/behoerden/amtsblatt-gesetze-entscheide/gesetzsammlung/systematische-srsz.html/72-416-413-1366-1365#anchor-19270" target="_blank">RS 231.110</a>) Art. 22, 23 und 25 Verfassung des eidgen&ouml;ssischen Standes Schwyz (<a href="https://www.sz.ch/behoerden/amtsblatt-gesetze-entscheide/gesetzsammlung/systematische-srsz.html/72-416-413-1366-1365#Anchor-42977" target="_blank">RS 100.000</a>) Art. 3&nbsp;Gesetz &uuml;ber die Organisation der Gemeinden und Bezirke (GOG,&nbsp;<a href="https://www.sz.ch/behoerden/amtsblatt-gesetze-entscheide/gesetzsammlung/systematische-srsz.html/72-416-413-1366-1365#Anchor-42977" target="_blank">RS 152.100</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(35, 'Remarques', 'Bemerkungen', '<p>In jeder Einwohnergemeinde wird ein Friedensrichter gew&auml;hlt. Der Friedensrichter ist die zust&auml;ndige Schlichtungsbeh&ouml;rde gem&auml;ss <a href="https://www.admin.ch/opc/fr/classified-compilation/20061121/index.html#a197" target="_blank">Artikel 197 der Schweizerischen Zivilprozessordnung (ZPO)</a>, sofern beide bzw. alle Parteien in derselben Gemeinde wohnen oder ihren Sitz haben (<a href="http://bgs.so.ch/frontend/versions/3854" target="_blank">Art. 4 und 5 Gesetz &uuml;ber die Gerichtsorganisation</a>).</p>', '<p>In jeder Einwohnergemeinde wird ein Friedensrichter gew&auml;hlt. Der Friedensrichter ist die zust&auml;ndige Schlichtungsbeh&ouml;rde gem&auml;ss&nbsp;<a href="https://www.admin.ch/opc/de/classified-compilation/20061121/index.html#a197" target="_blank">Artikel 197 der Schweizerischen Zivilprozessordnung (ZPO)</a>, sofern beide bzw. alle Parteien in derselben Gemeinde wohnen oder ihren Sitz haben (<a href="http://bgs.so.ch/frontend/versions/3854" target="_blank">Art. 4 und 5 Gesetz &uuml;ber die Gerichtsorganisation</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(36, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 4 ff., 8 ff., 13 ff., 23 ff. und 86 Gesetz &uuml;ber die Gerichtsorganisation (GO,&nbsp;<a href="http://bgs.so.ch/frontend/versions/4532" target="_blank">RS 125.12</a>) Art. 43 und 44 Verfassung des Kantons Solothurn (KV,&nbsp;<a href="http://bgs.so.ch/frontend/versions/4535" target="_blank">RS 111.1</a>) Verzeichnis der solothurnischen Gemeinden (<a href="http://bgs.so.ch/frontend/versions/4579" target="_blank">RS 131.3</a>)</p>', '<p>Art. 4 ff., 8 ff., 13 ff., 23 ff. und 86 Gesetz &uuml;ber die Gerichtsorganisation (GO,&nbsp;<a href="http://bgs.so.ch/frontend/versions/4532" target="_blank">RS 125.12</a>) Art. 43 und 44 Verfassung des Kantons Solothurn (KV,&nbsp;<a href="http://bgs.so.ch/frontend/versions/4535" target="_blank">RS 111.1</a>) Verzeichnis der solothurnischen Gemeinden (<a href="http://bgs.so.ch/frontend/versions/4579" target="_blank">RS 131.3</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(37, 'Remarques', 'Bemerkungen', '<p>Schlichtungsbeh&ouml;rden sind das Vermittlungsamt, die Schlichtungsstelle f&uuml;r Miet- und Pachtverh&auml;ltnisse, die Schlichtungsstelle f&uuml;r Arbeitsverh&auml;ltnisse, die Schlichtungsstelle f&uuml;r Klagen nach dem Gleichstellungsgesetz (<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/535" target="_blank">art. 1 Verordnung &uuml;ber die Schlichtungsbeh&ouml;rden</a>).</p>', '<p>Schlichtungsbeh&ouml;rden sind das Vermittlungsamt, die Schlichtungsstelle f&uuml;r Miet- und Pachtverh&auml;ltnisse, die Schlichtungsstelle f&uuml;r Arbeitsverh&auml;ltnisse, die Schlichtungsstelle f&uuml;r Klagen nach dem Gleichstellungsgesetz (<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/535" target="_blank">art. 1 Verordnung &uuml;ber die Schlichtungsbeh&ouml;rden</a>).</p>', 1, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00'),
(38, 'Bases légales', 'Rechtliche Grundlagen', '<p>Art. 3 ff. und 11 ff. des Gerichtsgesetzes (GG,&nbsp;<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/2224" target="_blank">RS 941.1</a>) Verordnung &uuml;ber die Schlichtungsbeh&ouml;rden (<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/535" target="_blank">RS 941.112</a>) Art. 2 des Einf&uuml;hrungsgesetzes zur Schweizerischen Zivilprozessordnung (EG-ZPO,&nbsp;<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/541" target="_blank">RS 961.2</a>) Art. 121 der Verfassung des Kantons St.Gallen (KV,&nbsp;<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/2187" target="_blank">RS 111.1</a>) Art. und et Anhang des Gemeindegesetzes (GG,&nbsp;<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/1798" target="_blank">RS 151.2</a>)</p>', '<p>Art. 3 ff. und 11 ff. des Gerichtsgesetzes (GG,&nbsp;<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/2224" target="_blank">RS 941.1</a>) Verordnung &uuml;ber die Schlichtungsbeh&ouml;rden (<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/535" target="_blank">RS 941.112</a>) Art. 2 des Einf&uuml;hrungsgesetzes zur Schweizerischen Zivilprozessordnung (EG-ZPO,&nbsp;<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/541" target="_blank">RS 961.2</a>) Art. 121 der Verfassung des Kantons St.Gallen (KV,&nbsp;<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/2187" target="_blank">RS 111.1</a>) Art. und et Anhang des Gemeindegesetzes (GG,&nbsp;<a href="https://www.gesetzessammlung.sg.ch/frontend/versions/1798" target="_blank">RS 151.2</a>)</p>', 2, NULL, '2018-11-05 23:00:00', '2018-11-05 23:00:00');
